[#<Recipe:0x00007fdbdf1b8f38 @id=1, @name="Charred Baby Bell Peppers with Burrata", @cook_time="25 minutes", @ingredients=[#<Ingredient:0x00007fdbdf1b8dd0 @id=1, @name="1/4 cup, plus 3 tablespoons extra virgin olive oil">, #<Ingredient:0x00007fdbdf1b8d80 @id=2, @name="2 tablespoons honey">, #<Ingredient:0x00007fdbdf1b8d30 @id=3, @name="juice of 1 lemon">, #<Ingredient:0x00007fdbdf1b8ce0 @id=4, @name="2 tablespoons champagne or white wine vinegar">, #<Ingredient:0x00007fdbdf1b8c90 @id=5, @name="2 tablespoons chopped fresh basil">, #<Ingredient:0x00007fdbdf1b8c40 @id=6, @name="2 tablespoons chopped fresh chives">, #<Ingredient:0x00007fdbdf1b8bf0 @id=7, @name="1 tablespoon chopped fresh thyme">, #<Ingredient:0x00007fdbdf1b8ba0 @id=8, @name="2 cloves garlic, grated">, #<Ingredient:0x00007fdbdf1b8b50 @id=9, @name="1 pinch each kosher salt, black pepper, and red pepper flakes">, #<Ingredient:0x00007fdbdf1b8b00 @id=10, @name="1 pound baby bell peppers">, #<Ingredient:0x00007fdbdf1b8ab0 @id=11, @name="1/2 teaspoon smoked paprika">, #<Ingredient:0x00007fdbdf1b8a60 @id=12, @name="1/2 teaspoon chili powder">, #<Ingredient:0x00007fdbdf1b8a10 @id=13, @name="1 loaf ciabatta bread, sliced">, #<Ingredient:0x00007fdbdf1b89c0 @id=14, @name="8 ounces burrata cheese, at room temperature">], @steps=[#<Step:0x00007fdbdf1b88f8 @id=1, @name="To make the vinaigrette. Combine 1/4 cup olive oil, the honey, pistachios, lemon juice, vinegar, basil, chives, thyme, garlic, and a pinch each of salt, pepper, and chili flakes in a glass jar and whisk until smooth. Taste and adjust the salt and pepper.">, #<Step:0x00007fdbdf1b88a8 @id=2, @name="Preheat the grill or grill pan to high.">, #<Step:0x00007fdbdf1b8858 @id=3, @name="In a large bowl, toss the peppers with 2 tablespoon olive oil, paprika, chili powder, salt and pepper. Place the bread on a baking sheet and drizzle with olive oil on both sides of each slice, season with salt.">, #<Step:0x00007fdbdf1b8808 @id=4, @name="Place the bread on the grill and grill both for about 2-3 minutes per side or until lightly toasted. Remove from the grill. At the same time, grill the peppers until softened and charred, 3-4 minutes per side. Remove from the grill and arrange on a serving plate.">, #<Step:0x00007fdbdf1b87b8 @id=5, @name="Drizzle the vinaigrette over the peppers, then break the balls of burrata around the peppers. Let everything sit 5-10 minutes. Serve with vinaigrette and grilled bread.">]>, #<Recipe:0x00007fdbdf1b8768 @id=2, @name="Roast Beef", @cook_time="2 hour 30 minutes", @ingredients=[#<Ingredient:0x00007fdbdf1b86c8 @id=1, @name="chicken">, #<Ingredient:0x00007fdbdf1b8678 @id=2, @name="Salt">], @steps=[#<Step:0x00007fdbdf1b8600 @id=1, @name="do stuff">, #<Step:0x00007fdbdf1b85b0 @id=2, @name="do more stuff">]>, #<Recipe:0x00007fdbdf1b8560 @id=3, @name="Random new recipe to delete", @cook_time="50 minutes", @ingredients=[#<Ingredient:0x00007fdbdf1b84c0 @id=1, @name="random ingredient">], @steps=[#<Step:0x00007fdbdf1b8448 @id=1, @name="Cook on the grill">]>, #<Recipe:0x00007fdbdf1b83f8 @id=4, @name="Random new recipe", @cook_time="50 minutes", @ingredients=[#<Ingredient:0x00007fdbdf1b8358 @id=1, @name="random ingredient">], @steps=[#<Step:0x00007fdbdf1b82e0 @id=2, @name="Cook on the grill">]>, #<Recipe:0x00007fdbdf1b8290 @id=5, @name="quatre", @cook_time="je ne sais pas", @ingredients=[#<Ingredient:0x00007fdbdf1b81f0 @id=2, @name="une">, #<Ingredient:0x00007fdbdf1b81a0 @id=3, @name="deux">, #<Ingredient:0x00007fdbdf1b8150 @id=4, @name="trois">], @steps=[#<Step:0x00007fdbdf1b80d8 @id=3, @name="hello">, #<Step:0x00007fdbdf1b8088 @id=4, @name="">, #<Step:0x00007fdbdf1b8038 @id=5, @name="">]>, #<Recipe:0x00007fdbce87bf98 @id=6, @name="si8zzzz", @cook_time="", @ingredients=[#<Ingredient:0x00007fdbce87bef8 @id=5, @name="">, #<Ingredient:0x00007fdbce87bea8 @id=6, @name="">, #<Ingredient:0x00007fdbce87be58 @id=7, @name="">], @steps=[#<Step:0x00007fdbce87bde0 @id=6, @name="">, #<Step:0x00007fdbce87bd90 @id=7, @